package com.company;import java.io.File;import java.io.IOException;import java.io.PrintWriter;import java.nio.file.Files;import java.nio.file.NoSuchFileException;import java.nio.file.Paths;import java.util.Arrays;import java.util.List;public class Main {    public static PrintWriter pw;    public static int X;    public static int serial;    public static void main(String[] args) throws IOException {        System.out.println("\nPlease wait, File reading started....\n");        /*read the data file from sentences.csv*/        try {            List<String> data = Files.readAllLines(Paths.get("sentences.csv"));            List<String> searchLIst = Files.readAllLines(Paths.get("search.txt"));            System.out.println("File read successfully....\n\nOutput.txt creating...\n");            int i = 0, X = 1;            /*if output file created for all the key word  then terminate the while loop*/            while (i < searchLIst.size()) {                    /* store the key word in local variable v */                String v = searchLIst.get(i);                StringBuilder match_text = new StringBuilder();                serial = 1;                    /*create new file for every key word as output(X).txt */                File file = new File("output" + X + ".txt");                pw = new PrintWriter(file);                    /*if file is not exist then create a new one for store the output */                if (!file.exists()) {                    file.createNewFile();                }                long start = System.currentTimeMillis();                    /*used forEach and lambdaExpression instead of loop */                data.forEach(value -> {                    String[] arr = value.split("\t");                    List<String> temp = Arrays.asList(arr);                    String temp1 = String.join(" ", temp.subList(2, temp.size()));                    if (temp1.contains(v)) {                        match_text.append(serial).append(". ").append(temp1).append("\n");                        serial++;                    }                });                long end = System.currentTimeMillis();                pw.println("Key word :  " + v + "\n" + "Time : " + (end - start) + " milliseconds\n");                    /*write all the match sentences to the outputX.txt file*/                pw.print(match_text);                System.out.println("output" + X + ".txt created successfully,,,,");                    /* finally close the file for every key word */                pw.close();                X++;                i++;            }            System.out.println("\nAll output generated....");        }catch (NoSuchFileException e){            System.out.println("File is not found....check the file name and path...."+e);        }    }}