package bd.edu.seu.model;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.ManyToOne;import java.time.LocalDateTime;@Data@Entity@AllArgsConstructor@NoArgsConstructorpublic class PriceHistory {    @Id    @GeneratedValue    private int historyId;    private LocalDateTime localDateTime;    private double pricePerUnit;    @ManyToOne    private Product Product;}