package bd.edu.seu.repository;import bd.edu.seu.model.Product;import bd.edu.seu.util.HibernateUtil;import org.hibernate.Session;import javax.print.attribute.standard.PrinterMessageFromOperator;import java.util.ArrayList;import java.util.Iterator;import java.util.List;public class ProductDaoMySQLimplementation implements ProductDao {    public List<Product> readAll() {        Session session = HibernateUtil.getSessionFactory().openSession();        session.beginTransaction();        List<Product> productList = new ArrayList<>();        return session.createQuery("from Product", Product.class).getResultList();    }    public Product findProductByid(long id) {        Session session = HibernateUtil.getSessionFactory().openSession();        session.beginTransaction();        Product product =  session.get(Product.class,id);        return product;    }    public Product save(Product product) {        Session session =HibernateUtil.getSessionFactory().openSession();        session.beginTransaction();        session.save(product);        session.getTransaction().commit();        return product;    }    @Override    public Product updateProduct(Product product) {        Session session = HibernateUtil.getSessionFactory().openSession();        session.beginTransaction();        session.update(product);        session.getTransaction().commit();        product = session.get(Product.class,product.getId());        session.close();        return product;    }}