package bd.edu.seu.util;import org.hibernate.SessionFactory;import org.hibernate.cfg.Configuration;public class HibernateUtil {    private static final SessionFactory sessionFactory = buildSessionFactoy();    private static SessionFactory buildSessionFactoy() {        try {            return new Configuration().configure("hibernate.cfg.xml").buildSessionFactory();        }catch (Throwable e){            throw new ExceptionInInitializerError(e);        }    }    public static SessionFactory getSessionFactory(){        return sessionFactory;    }    public static void shutdown(){        getSessionFactory().close();    }}