package model;public class Product {    private  int productId;    private  String productName;    private  String quantityPerUnit;    private  double unitPrice;    private  double unitsInStock;    private  double unitsOnOrder;    private  double reorderLevel;    private  boolean discontinued;    @Override    public String toString() {        return                "Id=" + productId +                ", Name='" + productName + '\'' +                ", qPUnit='" + quantityPerUnit + '\'' +                ", uPrice=" + unitPrice +                ", uInStock=" + unitsInStock +                ", uOnOrder=" + unitsOnOrder +                ", roLevel=" + reorderLevel +                ", dis_ed=" + discontinued ;    }    public int getProductId() {        return productId;    }    public String getProductName() {        return productName;    }    public String getQuantityPerUnit() {        return quantityPerUnit;    }    public double getUnitPrice() {        return unitPrice;    }    public double getUnitsInStock() {        return unitsInStock;    }    public double getUnitsOnOrder() {        return unitsOnOrder;    }    public double getReorderLevel() {        return reorderLevel;    }    public boolean getDiscontinued() {        return discontinued;    }    public Product(int productId, String productName, String quantityPerUnit, double unitPrice, double unitsInStock, double unitsOnOrder, double reorderLevel, boolean discontinued) {        this.productId = productId;        this.productName = productName;        this.quantityPerUnit = quantityPerUnit;        this.unitPrice = unitPrice;        this.unitsInStock = unitsInStock;        this.unitsOnOrder = unitsOnOrder;        this.reorderLevel = reorderLevel;        this.discontinued = discontinued;    }}