package repository;import java.io.FileReader;import java.io.IOException;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;import java.util.Properties;public class ConnectionSingleton {    FileReader fileReader;    Properties properties = new Properties();    private static Connection connection = null;    private static final ConnectionSingleton connectionSingleton = new ConnectionSingleton();    private ConnectionSingleton() {        try {            fileReader = new FileReader("D:\\Advancded Java\\Codes\\Product_System\\src\\main\\resources\\application.properties");            properties.load(fileReader);            connection = DriverManager.getConnection(                    properties.getProperty("DBURL") + "//" + properties.getProperty("HOSTNAME") + "/" + properties.getProperty("DBNAME")                    , properties.getProperty("USERNAME")                    , properties.getProperty("PASS"));            System.out.println("Successfully Connected...");        }        catch(SQLException | IOException e)        {            e.printStackTrace();        }    }    public static Connection getConnection() {        return connection;    }}