package repository;import java.io.*;import java.sql.*;import java.util.*;public class ConnectionSingleton {    private static String user;    private static String password;    private static String hostname;    private static String dbname;    private static String url;    private static Connection connection = null;    private static final ConnectionSingleton connectionSingleton = new ConnectionSingleton();    private void readConnectionParameters(){        String propertiesFileName = "application.properties";        try(InputStream inputStream = ConnectionSingleton.class.getClassLoader().getResourceAsStream(propertiesFileName)){            Properties properties = new Properties();            if(inputStream!=null){               properties.load(inputStream);               user = properties.getProperty("USER");               hostname = properties.getProperty("HOSTNAME");               password = properties.getProperty("PASS");               dbname = properties.getProperty("DBNAME");               url = properties.getProperty("DBURL")+"//"+hostname+"/"+dbname;            }        } catch (IOException e) {            e.printStackTrace();        }    }    private ConnectionSingleton() {        readConnectionParameters();        try {            connection = DriverManager.getConnection(url,user,password);        } catch (SQLException e) {            e.printStackTrace();        }    }    public static Connection getConnection() {        return connection;    }}