package repository;import model.Product;import java.util.List;public interface ProductDao {    public List<Product> readAll();    default Product getSingleProduct(int id){       return readAll().               stream().               filter(p -> p.getProductId()==id)               .findFirst()               .orElse(null);    }     void createProduct(Product product);     void updateProduct(Product product,int id);     void deleteProduct(int productid);     default void deleteAll(){}}