package repository;import java.io.IOException;import java.io.InputStream;import java.util.Properties;public class QuerySingleton {    private  static Properties properties;    private static QuerySingleton INSTANSE = new QuerySingleton();    private void readQueryParameters(){        String queryPropertiesFileName = "queries.properties";        try(InputStream inputStream = QuerySingleton.class.getClassLoader().getResourceAsStream(queryPropertiesFileName)) {            properties = new Properties();            if(inputStream!=null){                properties.load(inputStream);            }        } catch (IOException e) {            e.printStackTrace();        }    }    private QuerySingleton() {        readQueryParameters();    }    public static String getQuery(String query){        return properties.getProperty(query);    }}