package bd.edu.seu.repository;import bd.edu.seu.model.Student;import bd.edu.seu.util.HibernateUtil;import org.hibernate.Session;import java.util.ArrayList;import java.util.Iterator;import java.util.List;public class ProductDaoMySQLimplimentation implements ProductDao{    @Override    public Student saveStudent(Student student) {        Session session = HibernateUtil.getSesionFactory().openSession();        session.beginTransaction();        session.save(student);        session.getTransaction().commit();        session.close();        return student;    }    @Override    public List<Student> readAll() {        Session session = HibernateUtil.getSesionFactory().openSession();        session.beginTransaction();        Iterator<Student> iterator = session.createQuery("from student").list().iterator();        List<Student> studentList = new ArrayList<>();        while (iterator.hasNext()){            studentList.add(iterator.next());        }        session.close();        return studentList;    }    @Override    public List<Student> createStudents(List<Student> StudentList) {        long start = System.currentTimeMillis();        Session session = HibernateUtil.getSesionFactory().openSession();        session.beginTransaction();        for (Student student: StudentList){            session.save(student);        }        session.getTransaction().commit();        long end = System.currentTimeMillis();        System.out.println("Time"+((end-start)/1000)+"sec");        return StudentList;    }    @Override    public Student deleteStudent(int StudentId) {        return null;    }    @Override    public Student updateStudent(int StudentId, Student Student) {        return null;    }    @Override    public List<Student> deleteAll() {        return null;    }}