package bd.edu.seu.util;import org.hibernate.Session;import org.hibernate.SessionFactory;import org.hibernate.cfg.Configuration;public class HibernateUtil {    private static final SessionFactory sessionFactory = buildSessionFactory();    private static SessionFactory buildSessionFactory() {        try {            return new Configuration().configure().buildSessionFactory();        }catch (Throwable e){            throw new ExceptionInInitializerError(e);        }    }    public static SessionFactory getSesionFactory(){        return sessionFactory;    }    public static void shutDown(){        getSesionFactory().close();    }}