package bd.edu.seu.vaadindemooc.model;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.ManyToOne;import java.time.LocalDate;@Data@AllArgsConstructor@NoArgsConstructor@Entitypublic class StudentResult {    @Id    private long id;    private double cgpa;    private double gpa;    private long sem;    private LocalDate localDate;    @ManyToOne    private Student student;}