package bd.edu.seu.vaadindemooc.ui;import bd.edu.seu.vaadindemooc.model.Student;import bd.edu.seu.vaadindemooc.model.StudentAddress;import bd.edu.seu.vaadindemooc.model.StudentPhone;import bd.edu.seu.vaadindemooc.model.StudentResult;import bd.edu.seu.vaadindemooc.repository.StudentRepository;import bd.edu.seu.vaadindemooc.repository.StudentResultRepository;import com.vaadin.flow.component.grid.Grid;import com.vaadin.flow.component.orderedlayout.VerticalLayout;import com.vaadin.flow.router.Route;import java.time.LocalDate;@Route("student")public class StudentView extends VerticalLayout {    private StudentRepository studentRepository;    private StudentResultRepository studentResultRepository;    public StudentView(StudentRepository studentRepository,StudentResultRepository studentResultRepository) {        this.studentRepository=studentRepository;        this.studentResultRepository=studentResultRepository;        Student student = new Student();        student.setId(2017000000088l);        student.setName("Md. Joniyed Bhuiyan");        student.setDateOfBirth(LocalDate.now());        student.setPhone(new StudentPhone("+880","1688820107"));        student.setStudentAddress(new StudentAddress("Dharkhar","Akhaura","BrahmanBaria","Bangladesh"));        studentRepository.save(student);        StudentResult studentResult = new StudentResult();        studentResult.setId(1);        studentResult.setCgpa(3.9);        studentResult.setGpa(4.00);        studentResult.setSem(53);        studentResult.setStudent(student);        studentResult.setLocalDate(LocalDate.now());        studentResultRepository.save(studentResult);        studentResult.setId(2);        studentResult.setCgpa(3.89);        studentResult.setGpa(4.00);        studentResult.setSem(52);        studentResult.setStudent(student);        studentResult.setLocalDate(LocalDate.of(2019,8,6));        studentResultRepository.save(studentResult);        Grid<Student> studentGrid= new Grid<>(Student.class);        Grid<StudentResult> resultGrid= new Grid<>(StudentResult.class);        studentGrid.setItems(studentRepository.findAll());        resultGrid.setItems(studentResultRepository.findAll());        add(studentGrid,resultGrid);    }}