package bd.edu.seu.vaadingdemo.repository;import bd.edu.seu.vaadingdemo.model.Student;import org.springframework.data.jpa.repository.JpaRepository;import java.time.LocalDate;import java.util.List;public interface StudentRepository extends JpaRepository<Student,Long> {    public List<Student> findByName(String name);    public List<Student> findByNameContaining(String partial);    List<Student> findAllByLocalDateAfter(LocalDate localDate);    public void deleteById(Long id);}