package bd.edu.seu.vaadingdemo.ui;import com.vaadin.flow.component.button.Button;import com.vaadin.flow.component.datepicker.DatePicker;import com.vaadin.flow.component.orderedlayout.HorizontalLayout;import com.vaadin.flow.component.orderedlayout.VerticalLayout;import com.vaadin.flow.component.textfield.TextField;import com.vaadin.flow.router.Route;@Routepublic class MainView extends VerticalLayout {    public MainView(){        HorizontalLayout horizontalLayout = new HorizontalLayout();        TextField idField = new TextField("Student ID","Enter student ID..");        TextField nameField = new TextField("Student name","Enter student mame..");        TextField cgpaField = new TextField("Student cgpa","Enter student cgpa..");        DatePicker dateField = new DatePicker();        Button add = new Button("Add");        horizontalLayout.add(idField,nameField,cgpaField,dateField,add);        Button button = new Button("Click Me");        add(button);        add(horizontalLayout);    }}